define({"topics" : [{"title":"Field Mappings","shortdesc":"\n               <p class=\"shortdesc\">When you configure the MongoDB Lookup processor, you define the document fields to         look up in MongoDB. You map these\n                  document fields to fields in the record that contain the         values to look up.\n               </p>\n            ","href":"datacollector\/UserGuide\/Processors\/MongoDBLookup.html#concept_hw2_2mx_2fb","attributes": {"data-id":"concept_hw2_2mx_2fb",},"menu": {"hasChildren":false,},"tocID":"concept_hw2_2mx_2fb-d46e131554","topics":[]},{"title":"Lookup Cache","href":"datacollector\/UserGuide\/Processors\/MongoDBLookup.html#concept_btf_w22_ffb","attributes": {"data-id":"concept_btf_w22_ffb",},"menu": {"hasChildren":false,},"tocID":"concept_btf_w22_ffb-d46e131579","topics":[]},{"title":"Credentials","shortdesc":"\n               <p class=\"shortdesc\">Based on the authentication used by the MongoDB server, configure the processor to         use no authentication, username\/password\n                  authentication, or LDAP authentication. When using         username\/password authentication, you can also use delegated authentication.\n               </p>\n            ","href":"datacollector\/UserGuide\/Processors\/MongoDBLookup.html#concept_bbw_hg2_ffb","attributes": {"data-id":"concept_bbw_hg2_ffb",},"menu": {"hasChildren":false,},"tocID":"concept_bbw_hg2_ffb-d46e131611","topics":[]},{"title":"Read Preference","shortdesc":"\n               <p class=\"shortdesc\">You can configure the read preference that the MongoDB Lookup processor uses. </p>\n            ","href":"datacollector\/UserGuide\/Processors\/MongoDBLookup.html#concept_vsl_m32_ffb","attributes": {"data-id":"concept_vsl_m32_ffb",},"menu": {"hasChildren":false,},"tocID":"concept_vsl_m32_ffb-d46e131656","topics":[]},{"title":"BSON Timestamp ","href":"datacollector\/UserGuide\/Processors\/MongoDBLookup.html#concept_i1k_5k2_ffb","attributes": {"data-id":"concept_i1k_5k2_ffb",},"menu": {"hasChildren":false,},"tocID":"concept_i1k_5k2_ffb-d46e131711","topics":[]},{"title":"Enabling SSL\/TLS","href":"datacollector\/UserGuide\/Processors\/MongoDBLookup.html#task_gqy_hm2_ffb","attributes": {"data-id":"task_gqy_hm2_ffb",},"menu": {"hasChildren":false,},"tocID":"task_gqy_hm2_ffb-d46e131773","topics":[]},{"title":"Configuring a MongoDB Lookup","href":"datacollector\/UserGuide\/Processors\/MongoDBLookup.html#task_yt1_w4w_2fb","attributes": {"data-id":"task_yt1_w4w_2fb",},"menu": {"hasChildren":false,},"tocID":"task_yt1_w4w_2fb-d46e131845","topics":[]}]});