define({"topics" : [{"title":"Step 1. Install the Credential Store Stage Library","shortdesc":"\n                  <p class=\"shortdesc\">By default, a full <span class=\"ph\">Data Collector</span>         installation includes the Hashicorp Vault keystore credential store stage library. The core         installation\n                     does not include the library.\n                  </p>\n               ","href":"datacollector\/UserGuide\/Configuration\/CredentialStores.html#concept_kyt_lps_51b","attributes": {"data-id":"concept_kyt_lps_51b",},"menu": {"hasChildren":false,},"tocID":"concept_kyt_lps_51b-d46e22521","topics":[]},{"title":"Step 2. Configure the Credential Store Properties","href":"datacollector\/UserGuide\/Configuration\/CredentialStores.html#concept_bhn_4ps_51b","attributes": {"data-id":"concept_bhn_4ps_51b",},"menu": {"hasChildren":false,},"tocID":"concept_bhn_4ps_51b-d46e22650","topics":[]},{"title":"Step 3. Call Secrets from the Pipeline","shortdesc":"\n                  <p class=\"shortdesc\">Use the <code class=\"ph codeph\">credential:get()</code> or             <code class=\"ph codeph\">credential:getWithOptions()</code> function in pipeline stage properties to         retrieve secrets from Hashicorp Vault. \n                  </p>\n               ","href":"datacollector\/UserGuide\/Configuration\/CredentialStores.html#concept_sbq_vps_51b","attributes": {"data-id":"concept_sbq_vps_51b",},"menu": {"hasChildren":false,},"tocID":"concept_sbq_vps_51b-d46e22783","topics":[]}]});